package com.bdilab.colosseum.domain.run;

public class ApiRun {

    /*Output. Unique run ID. Generated by API server.*/
    private String id;

    /*Required input field. Name provided by user, or auto generated if run is created by scheduled job. Not unique.*/
    private String name;

    /*
    * storage_state: RunStorageState
    * RunStorageState: string , x âˆˆ { STORAGESTATE_AVAILABLE (default) , STORAGESTATE_ARCHIVED }
    * Output. Specify whether this run is in archived or available mode.
    */
    private String storage_state;

    private String description;

    /*Required input field. Describing what the pipeline manifest and parameters to use for the run.*/
    private ApiPipelineSpec pipeline_spec;

    /*
    * Optional input field.
    * Specify which resource this run belongs to. For Experiment, the only valid resource reference is a single Namespace.
    * ITEMS:apiResourceReference
    */
    private Object[] resource_references;

    /*Optional input field. Specify which Kubernetes service account this run uses.*/
    private String serviceAccount;

    /*Output. The time that the run created.*/
    private String createdAt;

    /*Output. When this run is scheduled to run. This could be different from created_at. For example, if a run is from a backfilling job that was supposed to run 2 month ago, the scheduled_at is 2 month ago, v.s. created_at is the current time.*/
    private String scheduledAt;

    /*Output. The time this run is finished.*/
    private String finishedAt;

    private String status;

    /*In case any error happens retrieving a run field, only run ID and the error message is returned. Client has the flexibility of choosing how to handle error. This is especially useful during listing call.*/
    private String error;

    /*Output. The metrics of the run. The metrics are reported by ReportMetrics API.*/
    /*ITEMS:apiRunMetric*/
    private Object[] metrics;

    public String getId() {
        return id;
    }

    public void setId(String id) {
        this.id = id;
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public String getStorage_state() {
        return storage_state;
    }

    public void setStorage_state(String storage_state) {
        this.storage_state = storage_state;
    }

    public String getDescription() {
        return description;
    }

    public void setDescription(String description) {
        this.description = description;
    }

    public ApiPipelineSpec getPipeline_spec() {
        return pipeline_spec;
    }

    public void setPipeline_spec(ApiPipelineSpec pipeline_spec) {
        this.pipeline_spec = pipeline_spec;
    }

    public Object[] getResource_references() {
        return resource_references;
    }

    public void setResource_references(Object[] resource_references) {
        this.resource_references = resource_references;
    }

    public String getServiceAccount() {
        return serviceAccount;
    }

    public void setServiceAccount(String serviceAccount) {
        this.serviceAccount = serviceAccount;
    }

    public String getCreatedAt() {
        return createdAt;
    }

    public void setCreatedAt(String createdAt) {
        this.createdAt = createdAt;
    }

    public String getScheduledAt() {
        return scheduledAt;
    }

    public void setScheduledAt(String scheduledAt) {
        this.scheduledAt = scheduledAt;
    }

    public String getFinishedAt() {
        return finishedAt;
    }

    public void setFinishedAt(String finishedAt) {
        this.finishedAt = finishedAt;
    }

    public String getStatus() {
        return status;
    }

    public void setStatus(String status) {
        this.status = status;
    }

    public String getError() {
        return error;
    }

    public void setError(String error) {
        this.error = error;
    }

    public Object[] getMetrics() {
        return metrics;
    }

    public void setMetrics(Object[] metrics) {
        this.metrics = metrics;
    }
}
